import React, { useState, useEffect } from "react";
import { Link, useParams, } from "react-router-dom";
import "./booking-page.css";

const CakeWithReviews = ({ url, title, description, max_reservations, price, review_description, stars }) => (
    <>
      <section className="container-cake">
        <img src={url} className="img-cake"></img>
        <h1>{title}</h1>
        <p>{description}</p>
        <p>
          <b>Guests:</b> {max_reservations}
        </p>
        <p>
          <b>Price:</b> {price} dkk
        </p>
      </section>
      <section className="review-container">
        <h1>Review</h1>
        <p>{review_description}</p>
        <p>{stars}</p>
      </section>
      <section className="form-container">
        <form class="fields">
          <input type="text" id="firstName" placeholder="First Name"></input>
          <input type="text" id="lastName" placeholder="Last Name"></input>
          <input type="text" id="email" placeholder="Email"></input>
          <input type="text" id="phone" placeholder="Phone"></input>
          <input
            type="number"
            id="number-guests"
            placeholder="Number of guests"
          ></input>
          <button id="submit" className="submit">
            Submit
          </button>
        </form>
      </section>
    </>
  );
  
  const CakeById = () => {
    const { id } = useParams();
  
    const [meals, setMeals] = useState();
    useEffect(() => {
      (async () => {
        const response = await fetch("http://localhost:3000/api/meals/" + id);
        const result = await response.json();
        setMeals(result);
      })();
    }, [id]);
    return (
      <div className="cake-container">
        {meals && meals.map((m) => <CakeWithReviews key={m.id} {...m} />)}
      </div>
    );
  };
