import React, { useState, useEffect } from "react";
import Background from "./img/background.jpg";
import Logo from "./img/homemade-logo.png";

import "./App.css";
import "./main.css";
import "./reset.css";

const Meal = ({ url, title, description, max_reservations, meal_time, price }) => (
  <div className="cake-container">
    <img src={url} className="img-cake"></img>
    <h1>{title}</h1>
    <p>{description}</p>
    <p><b>Guests:</b> {max_reservations}</p>
    <p><b>Price:</b> {price} dkk</p>
    <button id="get-cake" class="booking">Book now</button>
  </div>
);

const Meals = () => {
  const [meals, setMeals] = useState();

  useEffect(() => {
    (async () => {
      const response = await fetch("http://localhost:3000/api/meals");
      const result = await response.json();
      setMeals(result);
    })();
  }, []);
  return (
    <div className="cakes">
      {meals && meals.map((m) => <Meal key={m.id} {...m} />)}
    </div>
  );
  // return <pre>{JSON.stringify(meals, null, 2)}</pre>;
};

const Cake = ({ url, title, description, max_reservations, meal_time, price }) => (
  <div className="cake-container">
    <img src={url} className="img-cake"></img>
    <h1>{title}</h1>
    <p>{description}</p>
    <p><b>Guests:</b> {max_reservations}</p>
    <p><b>Price:</b> {price} dkk</p>
    <button id="get-cake" class="booking">Book now</button>
    <div className="review-container">
      <h1>Review</h1>
      <p>{review.description}</p>
      <p>{review.stars}</p>
      <form>
        <div class="fields">
          <input type="text" id="firstName" placeholder="First Name"> </input>
          <input type="text" id="lastName" placeholder="Last Name"> </input>
          <input type="text" id="email" placeholder="Email"> </input>
          <input type="text" id="phone" placeholder="Phone"> </input>
          <input type="number" id="number-guests" placeholder="Number of guests"> </input>
        </div>
      </form>
    </div>
  </div>
);

const CakeById = () => {
  const [meals, setMeals] = useState();

  useEffect(() => {
    (async () => {
      const response = await fetch("http://localhost:3000/api/meals/:id");
      const result = await response.json();
      setMeals(result);
    })();
  }, []);
  return (
    <div className="cakes">
      {meals && meals.map((m) => <Cake key={m.id} {...m} />)}
    </div>
  );
  // return <pre>{JSON.stringify(meals, null, 2)}</pre>;
};


function App() {
  return (
    <div className="App">
      <header className="header-container">
        <nav className="main-menu">
          <div className="left-menu">
            <p>
              <a href="#home">Home</a>
            </p>
            <p>
              <a href="#about">About</a>
            </p>
          </div>
          <div className="center-menu">
            <a href="#home">
              <img src={Logo} />
            </a>
          </div>
          <div className="right-menu">
            <p>
              <a href={CakeById}>Cakes</a>
            </p>
            <p>
              <a href="#recipes">Recipes</a>
            </p>
          </div>
        </nav>
        <img src={Background} alt="capcake pattern" />
        <div className="header-center">
          <input
            className="search-input"
            placeholder="Find your favorite cake"
          ></input>
        </div>
      </header>
      <Meals />
      <CakeById />
      <footer>
        <section className="copyright">Â© 2020 Victoria Kush</section>
      </footer>
    </div>
  );
}

export default App;
